mapper.select:
select

    distinct

'true' as QUERYID,

from t_job




    order by ${orderByClause}


    limit ${limitClause}

select

,

from t_job
where f_job_id = #{fJobId,jdbcType=VARCHAR}
and f_role = #{fRole,jdbcType=VARCHAR}
and f_party_id = #{fPartyId,jdbcType=VARCHAR}

mapper.delete:
delete from t_job
where f_job_id = #{fJobId,jdbcType=VARCHAR}
  and f_role = #{fRole,jdbcType=VARCHAR}
  and f_party_id = #{fPartyId,jdbcType=VARCHAR}
